frappe.web_form.after_load = () => {
    frappe.web_form.set_value('institute', 'Riphah International University');

    const nameFields = ['first_name', 'mother_name', 'father_name', 'guardian_name'];
    const alphaRegex = /^[A-Za-z]+(\.?[A-Za-z\s]+)*$/;
    const debounceTimers = {};

    nameFields.forEach(fieldname => {
        const input = $(`input[data-fieldname="${fieldname}"]`);
        const field = frappe.web_form.fields_dict[fieldname];

        if (!field.$wrapper.find('.custom-description').length) {
            field.$wrapper.append(`<div class="custom-description text-muted small" style="margin-top: 4px;"></div>`);
        }

        const descriptionDiv = field.$wrapper.find('.custom-description');

        input.on('input', function () {
            const value = $(this).val();

            // If field is empty or valid — immediately clear message
            if (value === '' || alphaRegex.test(value)) {
                clearTimeout(debounceTimers[fieldname]);
                $(this).removeClass('invalid');
                descriptionDiv.text('');
                return;
            }

            // Debounce only for showing errors
            clearTimeout(debounceTimers[fieldname]);
            debounceTimers[fieldname] = setTimeout(() => {
                $(this).addClass('invalid');
                descriptionDiv.text(`Only alphabets, spaces, and valid periods are allowed.`).css('color', 'red');
            }, 1000);
        });
    });

    const emailField = frappe.web_form.fields_dict['email_address'];
    if (emailField && !emailField.$wrapper.find('.custom-description').length) {
        emailField.$wrapper.append(`<div class="custom-description text-muted small" style="margin-top: 4px;"></div>`);
    }
    const emailInput = $(`input[data-fieldname="email_address"]`);
    const emailDescriptionDiv = emailField.$wrapper.find('.custom-description');
    const emailRegex = /^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/;

    let emailDebounceTimer;

    emailInput.on('input', function () {
        const value = $(this).val();

        // If email is empty or valid — clear message immediately
        if (value === '' || emailRegex.test(value)) {
            clearTimeout(emailDebounceTimer);
            $(this).removeClass('invalid');
            emailDescriptionDiv.text('');
            return;
        }

        // Debounce only for showing error
        clearTimeout(emailDebounceTimer);
        emailDebounceTimer = setTimeout(() => {
            $(this).addClass('invalid');
            emailDescriptionDiv.text(`Invalid Email Address`).css('color', 'red');
        }, 1000);
    });

    // ✅ Automatically set student type for medical students
    frappe.web_form.set_value('student_type', 'Medical');
};

frappe.web_form.on('gender', (field, value) => {
    const gender = frappe.web_form.get_value("gender");
    const institute = frappe.web_form.get_value("institute");
    const intended_level_of_study = frappe.web_form.get_value("intended_level_of_study");
    const preferred_city = frappe.web_form.get_value("preferred_city");
    load_filtered_programs(gender, institute, intended_level_of_study, preferred_city);
});

frappe.web_form.on('institute', (field, value) => {
    const gender = frappe.web_form.get_value("gender");
    const institute = frappe.web_form.get_value("institute");
    const intended_level_of_study = frappe.web_form.get_value("intended_level_of_study");
    const preferred_city = frappe.web_form.get_value("preferred_city");
    load_filtered_programs(gender, institute, intended_level_of_study, preferred_city);
});

frappe.web_form.on('intended_level_of_study', (field, value) => {
    const gender = frappe.web_form.get_value("gender");
    const institute = frappe.web_form.get_value("institute");
    const intended_level_of_study = frappe.web_form.get_value("intended_level_of_study");
    const preferred_city = frappe.web_form.get_value("preferred_city");
    load_filtered_programs(gender, institute, intended_level_of_study, preferred_city);
});

frappe.web_form.on('preferred_city', (field, value) => {
    const gender = frappe.web_form.get_value("gender");
    const institute = frappe.web_form.get_value("institute");
    const intended_level_of_study = frappe.web_form.get_value("intended_level_of_study");
    const preferred_city = frappe.web_form.get_value("preferred_city");
    load_filtered_programs(gender, institute, intended_level_of_study, preferred_city);
});

frappe.web_form.on('select_field_study_program', (field, value) => {
    frappe.web_form.set_value('proposed_program_of_study', value);
});

function load_filtered_programs(gender, institute, intended_level_of_study, preferred_city) {
    frappe.call({
        "method": "akf_crm.akf_crm.apis.webform_api.get_filtered_programs",
        "async": false,
        "args": {
            "gender": gender,
            "institute": institute,
            "intended_level_of_study": intended_level_of_study,
            "preferred_city": preferred_city
        },
        "callback": function (r) {
            const options = r.message;
            load_options(options);
        }
    });
}

function load_options(options) {
    $('select[data-fieldname="select_field_study_program"]').empty();
    $('select[data-fieldname="select_field_study_program"]').append(new Option("", ""));
    options.forEach(opt => {
        $('select[data-fieldname="select_field_study_program"]').append(new Option(opt.text, opt.value));
    });
}

